INSERT INTO [Topics] ([topic_id], [topic_name], [topic_content]) VALUES
('preparing-to-quit',                    N'Chuẩn bị để bỏ thuốc', N'Nếu bạn đang nghĩ đến việc bỏ thuốc lá, việc có những câu hỏi hoặc lo lắng về việc cai thuốc lá sẽ như thế nào là điều bình thường. ' + CHAR(13) + CHAR(10) + N'Tìm hiểu sự thật về nicotine và cách hút thuốc lá và thuốc lá điện tử ảnh hưởng đến bạn và những người xung quanh có thể giúp bạn cảm thấy sẵn sàng và tự tin hơn để thực hiện bước đầu tiên.'),
('smoking-and-your-health',             N'Hút thuốc và sức khỏe của bạn', N'Khoảnh khắc bạn bỏ hút thuốc, cơ thể bạn bắt đầu chữa lành và sức khỏe của bạn bắt đầu được cải thiện. Khám phá cách hút thuốc ảnh hưởng đến cơ thể của bạn, những rủi ro của việc hút thuốc thụ động và những gì xảy ra trong quá trình rút tiền khi bạn bắt đầu hành trình đến sức khỏe tốt hơn.'),
('smoking-and-pregnancy',               N'Hút thuốc và mang thai', N'Một số người ngừng hút thuốc ngay khi họ phát hiện ra họ đang mang thai. Những người khác cắt giảm, nhưng đấu tranh để bỏ thuốc lá. Một số người nhận thấy sự thôi thúc của họ và hút thuốc tăng trong khi mang thai.'),
('helping-friends-and-family-quit',     N'Giúp bạn bè và gia đình bỏ thuốc lá', N'Hút thuốc có thể gây hại cho những người xung quanh bạn, đặc biệt là trẻ em, vì cơ thể đang phát triển của chúng dễ bị ảnh hưởng của hút thuốc thụ động.'),
('cravings-triggers-and-routines',      N'Sự thèm muốn, nguyên nhân và thói quen', N'Ngừng hút thuốc là một trong những điều tốt nhất bạn có thể làm cho sức khỏe của mình - và bạn không phải làm điều đó một mình. Có rất nhiều công cụ và chiến thuật làm cho cảm giác thèm ăn và rút tiền dễ dàng hơn để quản lý, tăng cơ hội bỏ việc tốt.'),
('preparing-to-stop-smoking',           N'Chuẩn bị bỏ thuốc lá', N'Nếu ai đó bạn biết đang cố gắng bỏ hút thuốc hoặc vaping, cung cấp hỗ trợ là một trong những điều quan trọng nhất bạn có thể làm.'),
('vaping',                              N'Hút thuốc lá điện tử', N'Vapes (hoặc thuốc lá điện tử) thường được thúc đẩy như một sự thay thế ít gây hại cho hút thuốc, nhưng sự an toàn của chúng vẫn đang được nghiên cứu. Những ảnh hưởng sức khỏe lâu dài của vaping vẫn không chắc chắn và vapes chưa được chứng minh là một cách an toàn hoặc hiệu quả để bỏ thuốc lá hút thuốc.'),
('resources-for-health-professionals',  N'Tài nguyên cho Chuyên gia Y tế', N'Khi ai đó quyết định ngừng hút thuốc, mỗi chút hỗ trợ bạn có thể cung cấp cho họ. Các chuyên gia y tế và nhân viên cộng đồng có một vai trò quan trọng. Nghiên cứu đã chỉ ra rằng ngay cả lời khuyên ngắn gọn để bỏ hút thuốc từ một chuyên gia y tế cũng có thể tăng cơ hội bỏ thuốc thành công.')
INSERT INTO [quotes] ([content], [author]) VALUES
(N'Sức khỏe là tài sản quý giá nhất của chúng ta. Đừng để thuốc lá cướp đi điều đó.', N'Mark Twain'),
(N'Mỗi ngày không hút thuốc là một chiến thắng nhỏ dẫn đến thành công lớn.', N'Winston Churchill'),
(N'Ý chí mạnh mẽ có thể chinh phục mọi thói quen xấu. Bạn mạnh mẽ hơn bạn nghĩ.', N'Maya Angelou'),
(N'Tự do thực sự là khi bạn không còn nô lệ cho bất kỳ thói quen nào.', N'Nelson Mandela'),
(N'Hãy yêu thương cơ thể bạn. Nó là nơi duy nhất bạn có để sống.', N'Jim Rohn'),
(N'Thay đổi khó khăn ban đầu, lộn xộn ở giữa, nhưng tuyệt đẹp ở cuối.', N'Robin Sharma'),
(N'Bạn không cần phải hoàn hảo, bạn chỉ cần bắt đầu và kiên trì.', N'Tony Robbins'),
(N'Mỗi lần từ chối thuốc lá, bạn đang chọn sự sống thay vì từ từ chết.', N'Steve Jobs'),
(N'Sức mạnh không đến từ khả năng vật lý, mà từ ý chí bất khuất.', N'Mahatma Gandhi'),
(N'Hành trình ngàn dặm bắt đầu bằng một bước chân. Hôm nay là ngày bắt đầu.', N'Lao Tzu'),
(N'Đừng để thói quen của ngày hôm qua cản trở giấc mơ của ngày mai.', N'Oprah Winfrey'),
(N'Bạn có thể chịu đựng gần như mọi thứ nếu bạn có lý do đủ mạnh.', N'Friedrich Nietzsche'),
(N'Thành công không phải là cuối đích, thất bại không phải là chết người. Điều quan trọng là lòng can đảm tiếp tục.', N'Winston Churchill'),
(N'Cơ thể bạn xứng đáng được tôn trọng. Hãy nuôi dưỡng nó, đừng hủy hoại nó.', N'Buddha'),
(N'Mỗi phút không hút thuốc là một phút bạn đang tự chữa lành.', N'Deepak Chopra'),
(N'Ý chí của bạn mạnh hơn bất kỳ cơn thèm nào. Tin tương vào sức mạnh bên trong.', N'Tony Robbins'),
(N'Đôi khi điều nhỏ nhặt nhất tạo nên sự khác biệt lớn nhất trong cuộc đời bạn.', N'Dalai Lama'),
(N'Bạn không thể thay đổi quá khứ, nhưng bạn có thể tạo ra một tương lai khỏe mạnh.', N'Zig Ziglar'),
(N'Mỗi ngày mới là cơ hội để trở thành phiên bản tốt hơn của chính mình.', N'Ralph Waldo Emerson'),
(N'Sự kiên trì có thể biến thất bại thành thành tích phi thường.', N'Matt Biondi'),
(N'Hãy nhớ lý do tại sao bạn bắt đầu khi con đường trở nên khó khăn.', N'Paulo Coelho'),
(N'Thói quen tốt là chìa khóa của mọi thành công. Thói quen xấu là cánh cửa mở ra thất bại.', N'Og Mandino'),
(N'Bạn đủ mạnh để vượt qua điều này. Bạn đã vượt qua những khó khăn trước đây.', N'Maya Angelou'),
(N'Sức khỏe không phải là mọi thứ, nhưng không có sức khỏe thì mọi thứ đều không có ý nghĩa.', N'Schopenhauer'),
(N'Quyết định quan trọng nhất bạn có thể đưa ra là chăm sóc sức khỏe của mình.', N'Warren Buffett'),
(N'Đừng đợi đến ngày mai. Hôm nay là ngày hoàn hảo để bắt đầu cuộc sống mới.', N'Napoleon Hill'),
(N'Tự do khỏi thuốc lá là món quà tuyệt vời nhất bạn có thể tặng cho bản thân.', N'Benjamin Franklin'),
(N'Mỗi hơi thở sạch là một lời cảm ơn đến cơ thể bạn.', N'Thich Nhat Hanh'),
(N'Bạn không cần phải hoàn hảo, chỉ cần tiến bộ một chút mỗi ngày.', N'James Clear'),
(N'Cuộc sống quá ngắn ngủi để lãng phí cho những thói quen hủy hoại sức khỏe.', N'Steve Jobs');
